    /**
     * Removes a used recovery key.
     *
     * @since 5.2.0
     *
     * @param string $token The token used when generating a recovery mode key.
     */
    private function remove_key($token)
    {
        $records = $this->get_keys();
        if (!isset($records[$token])) {
            return;
        }
        unset($records[$token]);
        $this->update_keys($records);
    }
